export * from '@column-resizer/core';

export * from './container';
export * from './section';
export * from './bar';
import { ColumnResizer, ColumnResizerConfig } from '@column-resizer/core';
import * as React from 'react';

import { ColumnResizerContext } from '../context';
import { useIsomorphicLayoutEffect, useForwardedRef, useInitColumnResizer } from '../hooks';
import { HooksRenderer } from '../hooks-renderer';

import { useWatchColumnEvents, UseWatchColumnEventsConfig } from './use-watch-column-events';

export type ContainerProps = React.HTMLAttributes<HTMLDivElement> &
  UseWatchColumnEventsConfig &
  Partial<ColumnResizerConfig> & {
    columnResizerRef?: React.RefObject<ColumnResizer>;
  };

export const Container = React.forwardRef<HTMLDivElement, ContainerProps>(
  (
    {
      vertical = false,
      onActivate,
      beforeApplyResizer,
      afterResizing,
      columnResizerRef,
      style,
      ...props
    },
    ref,
  ) => {
    const containerRef = useForwardedRef<HTMLDivElement | null>(null, ref);
    const columnResizer = useInitColumnResizer({
      vertical,
      beforeApplyResizer,
    });

    useIsomorphicLayoutEffect(() => {
      columnResizer.init(containerRef.current);
      return () => columnResizer.dispose();
    }, [columnResizer]);

    React.useImperativeHandle(columnResizerRef, () => columnResizer, [columnResizer]);

    return (
      <ColumnResizerContext.Provider value={columnResizer}>
        <HooksRenderer
          hooks={useWatchColumnEvents}
          params={[containerRef, { onActivate, afterResizing }]}
        >
          {() => (
            <div ref={containerRef} {...props} style={columnResizer.styles.container(style)} />
          )}
        </HooksRenderer>
      </ColumnResizerContext.Provider>
    );
  },
);
import { ColumnResizer } from '@column-resizer/core';
import * as React from 'react';

export const ColumnResizerContext = React.createContext<ColumnResizer | null>(null);
import React, { MutableRefObject, Ref } from 'react';
import { ColumnResizerEventMap, ColumnResizer, ColumnResizerConfig } from '@column-resizer/core';

import { ColumnResizerContext } from './context';

export const useIsomorphicLayoutEffect =
  typeof window === 'object' ? React.useLayoutEffect : React.useEffect;

export function useInitColumnResizer({
  vertical,
  beforeApplyResizer: beforeApplyResizer_,
}: ColumnResizerConfig) {
  const beforeApplyResizer = useRefCallback(beforeApplyResizer_);

  return React.useMemo(
    () => new ColumnResizer({ vertical, beforeApplyResizer }),
    [vertical, beforeApplyResizer],
  );
}

export function useColumnResizer() {
  return React.useContext(ColumnResizerContext)!;
}

export function useColumnResizerEvent<E extends Element, K extends keyof ColumnResizerEventMap>(
  elmRef: React.RefObject<E>,
  key: K,
  callback?: (event: CustomEvent<ColumnResizerEventMap[K]>) => void,
) {
  const columnResizer = useColumnResizer();
  const refCallback = useRefCallback(callback);

  React.useEffect(
    () => columnResizer.on(elmRef.current, key, (event) => refCallback(event)),
    [columnResizer, key, elmRef, refCallback],
  );
}

export function useForwardedRef<T>(
  initialValue: T,
  refToForward: Ref<T> | undefined,
): MutableRefObject<T> {
  const ref = React.useRef(initialValue);

  React.useImperativeHandle(refToForward, () => ref.current!);

  return ref;
}

type AnyFunc = (...params: any[]) => any;
export function useRefCallback<T extends AnyFunc>(callback?: T): T;
export function useRefCallback(callback?: AnyFunc): AnyFunc {
  const callbackRef = React.useRef(callback);

  callbackRef.current = callback;

  return React.useCallback(
    (...params: unknown[]) => callbackRef.current?.(...params),
    [callbackRef],
  );
}
import React from 'react';

export type HooksRendererProps<T extends (...args: any[]) => any> = {
  hooks: T;
  params: Parameters<T>;
  children: (result: ReturnType<T>) => React.ReactNode;
};

export function HooksRenderer<T extends (...args: any[]) => any>({
  hooks,
  params,
  children,
}: HooksRendererProps<T>) {
  const result = hooks(...params);

  return <>{children(result)}</>;
}
import * as React from 'react';
import { useColumnResizerEvent } from '../hooks';

export type UseWatchColumnEventsConfig = {
  onActivate?: () => void;
  afterResizing?: () => void;
};

export function useWatchColumnEvents(
  ref: React.MutableRefObject<HTMLDivElement | null>,
  { onActivate, afterResizing }: UseWatchColumnEventsConfig,
) {
  useColumnResizerEvent(ref, 'column:activate', onActivate);
  useColumnResizerEvent(ref, 'column:after-resizing', afterResizing);
}
import { ColumnSectionConfig } from '@column-resizer/core';
import * as React from 'react';

import { useForwardedRef, useColumnResizer, useColumnResizerEvent } from '../hooks';

export type SectionProps = React.HTMLAttributes<HTMLDivElement> &
  ColumnSectionConfig & {
    onSizeChanged?: (currentSize: number) => void;
  };

export const Section = React.forwardRef<HTMLDivElement, SectionProps>(
  (
    { defaultSize, size, disableResponsive, minSize, maxSize, onSizeChanged, style, ...props },
    innerRef,
  ) => {
    const ref = useForwardedRef<HTMLDivElement | null>(null, innerRef);
    const columnResizer = useColumnResizer();
    const config = { defaultSize, size, disableResponsive, minSize, maxSize };

    useColumnResizerEvent(ref, 'section:size-change', (e) => onSizeChanged?.(e.detail.size));

    return (
      <div
        ref={ref}
        {...props}
        style={columnResizer.styles.section(config, style)}
        {...columnResizer.attributes.section(config)}
      />
    );
  },
);
import { ColumnBarConfig } from '@column-resizer/core';

import * as React from 'react';

import { useColumnResizer, useColumnResizerEvent, useForwardedRef } from '../hooks';
import { StyledInteractiveArea, ExpandInteractiveArea } from './styled';

export type { ExpandInteractiveArea };

export type BarProps = Omit<React.HTMLAttributes<HTMLDivElement>, 'onClick'> &
  Partial<ColumnBarConfig> & {
    expandInteractiveArea?: ExpandInteractiveArea;
    onClick?: () => void;
    onStatusChanged?: (isActive: boolean) => void;
  };

export const Bar = React.forwardRef<HTMLDivElement, BarProps>(
  (
    { children, onClick, expandInteractiveArea, onStatusChanged, style, size = 10, ...props },
    innerRef,
  ) => {
    const columnResizer = useColumnResizer();
    const ref = useForwardedRef<HTMLDivElement | null>(null, innerRef);
    const config = { size };

    useColumnResizerEvent(ref, 'bar:click', () => onClick?.());
    useColumnResizerEvent(ref, 'bar:status-change', (e) => onStatusChanged?.(e.detail.isActive));

    return (
      <div
        ref={ref}
        {...props}
        style={columnResizer.styles.bar(config, { ...style, position: 'relative' })}
        {...columnResizer.attributes.bar(config)}
      >
        {children}
        <StyledInteractiveArea
          {...expandInteractiveArea}
          vertical={columnResizer.config.vertical}
        />
      </div>
    );
  },
);
import * as React from 'react';

export type ExpandInteractiveArea = {
  top?: number;
  left?: number;
  right?: number;
  bottom?: number;
};

export type StyledInteractiveAreaProps = React.HTMLAttributes<HTMLDivElement> &
  ExpandInteractiveArea & {
    vertical: boolean;
  };

export const StyledInteractiveArea = React.forwardRef<HTMLDivElement, StyledInteractiveAreaProps>(
  ({ top = 0, right = 0, bottom = 0, left = 0, vertical, style: propsStyle, ...props }, ref) => {
    const style = React.useMemo(
      (): React.CSSProperties => ({
        position: 'absolute',
        top: -top,
        left: -left,
        right: -right,
        bottom: -bottom,
        WebkitTapHighlightColor: 'transparent',
        userSelect: 'none', // disable ios long press popup
        ...propsStyle,
      }),
      [propsStyle, top, left, right, bottom, vertical],
    );

    return <div {...props} style={style} ref={ref} />;
  },
);
